package Liste;public class Knoten {    private String value;    private Knoten left;    private Knoten right;    public static int position = 0;    public Knoten(String value){        this.value = value;        left = null;        right = null;        position++;    }    public Knoten(String value, Knoten left, Knoten right) {        this.value = value;        this.left = left;        this.right = right;        position++;    }    public String toString(){        return "" + position + ": " + value;    }}