/*@Author Max Danigel */package liste;import java.time.LocalDate;import java.time.LocalTime; // Für das Datum und die Uhrzeit bei der erstellung der To-Do.public class Knoten {    private String value;     Knoten left;     Knoten right;    private int priority;    private LocalDate date;    private LocalTime time;    public Knoten(String value, int priority){ // Default Konstruktor, der jedes mal mit einem Scanner die Eingabe überprüft        setValue(value);        setPriority(priority);        left = null;        right = null;        time = LocalTime.now().withSecond(0).withNano(0); // Setzt Sekunden und Nano direkt auf 0, damit sie nicht mit geprintet werden.        this.date = LocalDate.now();        //TODO: Datum und Uhrzeit noch weiter anpassen, bis es schön aussieht.    }    /*    @Setter Legt den Wert für die To-Do fest.    @Exception Überprüft ob etwas in das To-Do Feld eingegeben wurde, oder ob es mit null belegt wurde.     */    public void setValue(String value)throws NullPointerException, IllegalArgumentException{        if(value == null){            throw new NullPointerException("Du kannst keine To-Do mit null belegen!");        }        if(value.equals("")){            throw new IllegalArgumentException("Du hast vergessen etwas einzutragen!");        }        this.value = value;    }    /*    @Setter Legt die Priorität der To-Do fest.    @Exception Überprüft ob der Wert zwischen 1-3 liegt.     */    public void setPriority(int priority)throws IllegalArgumentException{        if(priority <= 0 || priority > 3){            throw new IllegalArgumentException("Der Wert darf nicht unter 1 oder über 3 sein!");        }        this.priority = priority;    }    /*    @ToString Wie das Objekt auf dem Terminal geprintet wird.     */    public String toString(){        return " " + value + " + Priorität: " + priority + " | Erstellt am: " + date + " | Um: " + time + "\n";    }    public void right() {    }}