package Liste;import java.time.LocalTime;  // Für die Lokale Uhrzeit, wann eine ToDo eingefügt worden ist.import java.time.LocalDate; // Für das Lokale Datum, an welchen Tag die ToDo erstellt worden ist.import java.util.Scanner; // Ein Scanner, der überprüft was der Benutzer eingibt.public class Knoten {    private String value;    private Knoten left;    private Knoten right;    public static int position = 0;    public Knoten(String value){        this.value = value;        left = null;        right = null;        position++;    }    public Knoten(String value, Knoten left, Knoten right) {        this.value = value;        this.left = left;        this.right = right;        position++;    }    public String toString(){        return "" + position + ": " + value + "hallo";    }}