package liste.knoten;import java.time.LocalDateTime;import java.util.Scanner;public class Knoten {    private String value;    private Knoten left;    private Knoten right;    private int priority;    private LocalDateTime date;    public static int position = 0;    public Knoten(String value){        this.value = value;        left = null;        right = null;        position++;        date = LocalDateTime.now();    }    public Knoten(String value, Knoten left, Knoten right) {        this.value = value;        this.left = left;        this.right = right;        position++;    }    public String toString(){        return "" + position + ": " + value + " Erstellt am: " + date + " Priorit√§t: " + priority;    }}