/*@Author Max Danigel */package liste.knoten;import java.time.LocalDateTime; // Für das Datum und die Uhrzeit bei der erstellung der To-Do.import java.util.Scanner; // Nimmt die Eingabe entgegen für value und priority.public class Knoten {    private String value;    private Knoten left;    private Knoten right;    private int priority;    private LocalDateTime date;    private int position = 0; // Counter der erst belegt wird, wenn das Objekt auf dem Speicher gelegt wird.    private Scanner scanner;    public Knoten(){ // Default Konstruktor, der jedes mal mit einem Scanner die Eingabe überprüft        this.scanner = new Scanner(System.in);        setValue();        setPriority();        left = null;        right = null;        position++;        date = LocalDateTime.now();    }    /*    @Setter Legt den Wert für die To-Do fest.    @Exception Überprüft ob etwas in das To-Do Feld eingegeben wurde, oder ob es mit null belegt wurde.     */    public void setValue()throws NullPointerException, IllegalArgumentException{        System.out.println("neue To Do: ");        this.value = scanner.nextLine();        if(value == null){            throw new NullPointerException("Du kannst keine To-Do mit null belegen!");        }        if(value.equals("")){            throw new IllegalArgumentException("Du hast vergessen etwas einzutragen!");        }    }    /*    @Setter Legt die Priorität der To-Do fest.    @Exception Überprüft ob der Wert zwischen 1-3 liegt.     */    public void setPriority()throws IllegalArgumentException{        System.out.println("Lege zwischen 1-3 die Priorität fest: ");        this.priority = scanner.nextInt();        if(priority <= 0 || priority > 3){            throw new IllegalArgumentException("Der Wert darf nicht unter 1 oder über 3 sein!");        }    }    /*    @ToString Wie das Objekt auf dem Terminal geprintet wird.     */    public String toString(){        return "" + position + ": " + value + " | Erstellt am: " + date + " | Priorität: " + priority;    }}